'use strict';

var Base = require('../../base');
var util = require('util');
module.exports = Files;

function Files () {
  Base.apply(this, arguments);
}

util.inherits(Files, Base);

Files.prototype.urlPath = 'files';

Files.prototype.Model = require('../../../models/context/version/file');

// This is use to differentiate context/files from version/files in the browser store
// the browser store guarantees only one instance of each type of object will ever exist
Files.prototype.storeName = 'VersionFiles';