'use strict';

module.exports = Store;

function Store (opts) {
  this.enabled = opts.enabled;
  this.hash = {};
}

Store.prototype.get = function (key) {
  if (this.enabled) {
    return this.hash[key];
  }
};

Store.prototype.set = function (key, val) {
  if (this.enabled) {
    this.hash[key] = val;
    return this.hash[key];
  }
};